import React, { Component } from 'react';
import pokedex from './Pokedex';

function randomCard(deck) {
  const card = Math.floor(Math.random() * deck.length);
  return deck[card];
}

class Pokecard extends Component {
  render() {
    return <div>{randomCard(pokedex)}</div>;
  }
}
